protectedSettings.script:
[base64(concat('#!/bin/bash
set -e
export DEBIAN_FRONTEND=noninteractive
echo "Starting ClawLink Agent setup..."

# Install Node.js 22
curl -fsSL https://deb.nodesource.com/setup_22.x | bash -
apt-get install -y nodejs

# Create agent directory
mkdir -p /opt/clawlink-agent
cd /opt/clawlink-agent

# Create package.json
cat > package.json << PKGEOF
{"name":"clawlink-agent","version":"1.0.0","main":"agent.js","dependencies":{"ws":"^8.16.0"}}
PKGEOF

# Create agent.js
cat > agent.js << AGENTEOF
const http = require("http");
const https = require("https");
const crypto = require("crypto");
const { WebSocketServer } = require("ws");

const PORT = parseInt(process.env.CLAWLINK_PORT || "8443");
const TOKEN = process.env.CLAWLINK_TOKEN || "";
const OPENAI_ENDPOINT = process.env.OPENCLAW_AI_ENDPOINT || "";
const OPENAI_KEY = process.env.OPENAI_KEY || "";
const MODEL = process.env.OPENCLAW_MODEL || "gpt-4o-mini";

const clients = new Map();

const server = http.createServer((req, res) => {
  if (req.url === "/health") {
    res.writeHead(200, { "Content-Type": "application/json" });
    res.end(JSON.stringify({ status: "ok", clients: clients.size }));
    return;
  }
  res.writeHead(404);
  res.end();
});

const wss = new WebSocketServer({ server, path: "/tunnel" });

wss.on("connection", (ws, req) => {
  const auth = req.headers["authorization"];
  if (auth !== "Bearer " + TOKEN) {
    console.log("Unauthorized connection attempt");
    ws.close(4001, "Unauthorized");
    return;
  }
  const clientId = crypto.randomUUID();
  clients.set(clientId, ws);
  console.log("Client connected:", clientId);
  ws.send(JSON.stringify({ type: "connected", payload: { clientId } }));

  ws.on("message", async (data) => {
    try {
      const msg = JSON.parse(data.toString());
      await handleMessage(ws, msg);
    } catch (e) {
      ws.send(JSON.stringify({ type: "error", payload: { message: e.message } }));
    }
  });

  ws.on("close", () => {
    clients.delete(clientId);
    console.log("Client disconnected:", clientId);
  });
});

async function handleMessage(ws, msg) {
  switch (msg.type) {
    case "ping":
      ws.send(JSON.stringify({ type: "pong", id: msg.id }));
      break;
    case "session.message":
      await handleChat(ws, msg);
      break;
    case "session.status":
      ws.send(JSON.stringify({ type: "response", id: msg.id, payload: { instance: "clawlink-vm", model: MODEL, uptime: process.uptime() } }));
      break;
    default:
      ws.send(JSON.stringify({ type: "error", id: msg.id, payload: { message: "Unknown type: " + msg.type } }));
  }
}

async function handleChat(ws, msg) {
  const content = msg.payload?.content;
  if (!content) {
    ws.send(JSON.stringify({ type: "error", id: msg.id, payload: { message: "Missing content" } }));
    return;
  }
  try {
    const response = await callOpenAI(content);
    ws.send(JSON.stringify({ type: "response", id: msg.id, payload: { content: response } }));
  } catch (e) {
    ws.send(JSON.stringify({ type: "error", id: msg.id, payload: { message: e.message } }));
  }
}

function callOpenAI(userMessage) {
  return new Promise((resolve, reject) => {
    const url = new URL(OPENAI_ENDPOINT + "/openai/deployments/" + MODEL + "/chat/completions?api-version=2024-02-15-preview");
    const body = JSON.stringify({ messages: [{ role: "user", content: userMessage }], max_tokens: 1000 });
    const req = https.request(url, { method: "POST", headers: { "Content-Type": "application/json", "api-key": OPENAI_KEY } }, (res) => {
      let data = "";
      res.on("data", (chunk) => data += chunk);
      res.on("end", () => {
        try {
          const json = JSON.parse(data);
          if (json.error) reject(new Error(json.error.message));
          else resolve(json.choices?.[0]?.message?.content || "No response");
        } catch (e) {
          reject(new Error("Parse error: " + data.substring(0, 200)));
        }
      });
    });
    req.on("error", reject);
    req.write(body);
    req.end();
  });
}

server.listen(PORT, "0.0.0.0", () => {
  console.log("ClawLink Agent listening on port " + PORT);
  console.log("OpenAI Endpoint: " + OPENAI_ENDPOINT);
  console.log("Model: " + MODEL);
});
AGENTEOF

# Install dependencies
npm install

# Set ownership
chown -R ', parameters('adminUsername'), ':', parameters('adminUsername'), ' /opt/clawlink-agent

# Create environment file
cat > /opt/clawlink-agent/.env << ENVEOF
OPENAI_KEY=', listKeys(resourceId('Microsoft.CognitiveServices/accounts', variables('openAiName')), '2023-05-01').key1, '
ENVEOF
chmod 600 /opt/clawlink-agent/.env
chown ', parameters('adminUsername'), ':', parameters('adminUsername'), ' /opt/clawlink-agent/.env

# Create systemd service
cat > /etc/systemd/system/clawlink-agent.service << SVCEOF
[Unit]
Description=ClawLink Tunnel Agent
After=network-online.target
Wants=network-online.target

[Service]
Type=simple
User=', parameters('adminUsername'), '
WorkingDirectory=/opt/clawlink-agent
Environment=NODE_ENV=production
Environment=CLAWLINK_PORT=', parameters('tunnelPort'), '
Environment=CLAWLINK_TOKEN=', parameters('tunnelToken'), '
Environment=OPENCLAW_AI_ENDPOINT=https://', variables('openAiName'), '.openai.azure.com
Environment=OPENCLAW_MODEL=', parameters('openAiModel'), '
EnvironmentFile=/opt/clawlink-agent/.env
ExecStart=/usr/bin/node /opt/clawlink-agent/agent.js
Restart=always
RestartSec=10

[Install]
WantedBy=multi-user.target
SVCEOF

systemctl daemon-reload
systemctl enable clawlink-agent
systemctl start clawlink-agent

cat > /home/', parameters('adminUsername'), '/CLAWLINK_READY.txt << READYEOF
=====================================
  ClawLink Agent Ready!
=====================================
Tunnel Port: ', parameters('tunnelPort'), '
Model: ', parameters('openAiModel'), '

Check status: sudo systemctl status clawlink-agent
View logs: sudo journalctl -u clawlink-agent -f
READYEOF
chown ', parameters('adminUsername'), ':', parameters('adminUsername'), ' /home/', parameters('adminUsername'), '/CLAWLINK_READY.txt
echo "ClawLink Agent setup complete!"
'))]
